class ModalNavigator{currentValue=0;elementsToDisplay=[];init(e,t){this.currentValue=e,this.elementsToDisplay=t}reset(){this.currentValue=0,this.elementsToDisplay=[]}goToNextIndex(){return this.currentValue<this.elementsToDisplay.length-1&&this.currentValue>=0?this.currentValue++:this.currentValue===this.elementsToDisplay.length-1&&(this.currentValue=0),this.getCurrentImg()}goToPreviousIndex(){return this.currentValue>=1?this.currentValue--:0===this.currentValue&&(this.currentValue=this.elementsToDisplay.length-1),this.getCurrentImg()}getCurrentImg(){return this.elementsToDisplay[this.currentValue]}}export const navigator=new ModalNavigator;