let data=await fetch("./data.json");export const pictures=await data.json();export let imgsArrayToDisplay=pictures;let modalPlace=document.querySelector(".zoomDisplay");export const listenImgForModalNavigation=()=>{let e=document.querySelectorAll(".gallery img"),l=0;for(let a of e)a.addEventListener("click",e=>{showModal(!0);let a=e.target.id;createImgElement(imgsArrayToDisplay[l=imgsArrayToDisplay.findIndex(e=>e.id===a)],modalPlace)});document.getElementById("previousModalButton").addEventListener("click",()=>{l>=1?createImgElement(imgsArrayToDisplay[l-=1],modalPlace):0===l&&createImgElement(imgsArrayToDisplay[l=imgsArrayToDisplay.length-1],modalPlace)}),document.getElementById("nextModalButton").addEventListener("click",()=>{l<imgsArrayToDisplay.length-1&&l>=0?createImgElement(imgsArrayToDisplay[l+=1],modalPlace):l===imgsArrayToDisplay.length-1&&createImgElement(imgsArrayToDisplay[l=0],modalPlace)})};let createImgElement=(e,l)=>{modalPlace.innerHTML="";let a=`<img class='gallery-item ${e.category}' src='${e.url}' alt='${e.alt}' id='${e.id} loading='lazy'>`;l.innerHTML=a};export const displayPicturesInGallery=e=>{let l=document.querySelector(".gallery");l.innerHTML="";let a="";if(0===e.length){let t=document.createElement("p");t.innerText="Pas d'images afficher"}for(let r of e)a+=`<img class='gallery-item ${r.category}' src='${r.smallUrl}' alt='${r.alt}' id='${r.id}' loading='lazy'>`;l.innerHTML=a,listenImgForModalNavigation()};export const ListenButtonsToFilterResult=e=>{let l=document.querySelectorAll(".gallery_buttons button");for(let a of l)a.addEventListener("click",()=>{imgsArrayToDisplay=e.filter(e=>e.category===a.className),displayPicturesInGallery(imgsArrayToDisplay)});document.querySelector(".gallery_buttons .tous").addEventListener("click",()=>{imgsArrayToDisplay=e,displayPicturesInGallery(imgsArrayToDisplay)})};export const showModal=(e=!0)=>{document.querySelector(".zoomDisplay").innerHTML="";let l=document.querySelector(".modalBackground");l.style.display=e?"flex":"none"};